targets.expr:
(
  sum(increase(core_tracker_success_duties_total{
    cluster_name="$cluster_name",
    cluster_hash="$cluster_hash",
    duty=~"attester|proposer|sync_message"
  }[$__range])) 
  by (duty)
)
/
(
  sum(increase(core_tracker_expect_duties_total{
    cluster_name="$cluster_name",
    cluster_hash="$cluster_hash",
    duty=~"attester|proposer|sync_message"
  }[$__range])) 
  by (duty)
) > 0

avg by (cluster_name, cluster_hash, duty) (
  rate(core_consensus_duration_seconds_sum{
    cluster_name="$cluster_name",
    cluster_hash="$cluster_hash",
    duty=~"$duty"
  }[$interval])
  /
  rate(core_consensus_duration_seconds_count{
    cluster_name="$cluster_name",
    cluster_hash="$cluster_hash",
    duty=~"$duty"
  }[$interval])
)
